func:
if (msg.payload >= 0 && msg.payload <= 78){
    //red
    msg.lcdColor = {r: 255, g: 0, b: 0};
}
else if (msg.payload > 78 && msg.payload <= 156){
    //orange
    msg.lcdColor = {r: 255, g: 128, b: 0};
}
else if (msg.payload > 156 && msg.payload <= 234){
    //yellow
    msg.lcdColor = {r: 255, g: 255, b: 0};
}
else if (msg.payload > 234 && msg.payload <= 312){
    //red-green
    msg.lcdColor = {r: 128, g: 255, b: 0};
}
else if (msg.payload > 312 && msg.payload <= 390){
    //green
    msg.lcdColor = {r: 0, g: 255, b: 0};
}
else if (msg.payload > 390 && msg.payload <= 468){
    //blue-green
    msg.lcdColor = {r: 0, g: 255, b: 128};
}
else if (msg.payload > 468 && msg.payload <= 546){
    //green-blue
    msg.lcdColor = {r: 0, g: 255, b: 255};
}
else if (msg.payload > 546 && msg.payload <= 624){
    //light-blue
    msg.lcdColor = {r: 0, g: 128, b: 255};
}
else if (msg.payload > 624 && msg.payload <= 702){
    //blue
    msg.lcdColor = {r: 0, g: 0, b: 255};
}
else if (msg.payload > 702 && msg.payload <= 780){
    //purple
    msg.lcdColor = {r: 127, g: 0, b: 255};
}
else if (msg.payload > 780 && msg.payload <= 858){
    //pink-purple
    msg.lcdColor = {r: 255, g: 0, b: 255};
}
else if (msg.payload > 858 && msg.payload <= 936){
    //pink
    msg.lcdColor = {r: 255, g: 0, b: 127};
}
else if (msg.payload > 936 && msg.payload <= 1023){
    //gray
    msg.lcdColor = {r: 128, g: 128, b: 128};
}

//don't display rotary value on lcd
msg.lcdCursor = {row: 1, column: 0};
msg.payload = '';

return msg;
